import static org.junit.Assert.assertEquals;

import java.io.File;
import java.io.FileNotFoundException;
import java.io.IOException;

import org.junit.Test;

public class SVGPng64ContainerTest {
	private static final File PNG_BASE_DIR = new File("src/test/resources");

	@Test
	public void testReplaceXRefToBase64() throws FileNotFoundException, IOException {
		String svg = "<svg id=\"example1\" xmlns=\"http://www.w3.org/2000/svg\" xmlns:xlink=\"http://www.w3.org/1999/xlink\"><image x=\"0\" y=\"0\" width=\"5\" height=\"5\" xlink:href=\"./sample.png\"/></svg>";
		String expects = "<svg id=\"example1\" xmlns=\"http://www.w3.org/2000/svg\" xmlns:xlink=\"http://www.w3.org/1999/xlink\"><image x=\"0\" y=\"0\" width=\"5\" height=\"5\" xlink:href=\"data:image/png;base64,\"/></svg>";
		assertEquals("replace path to base64", expects, SVGPng64Container.replaceXRefToBase64(svg, PNG_BASE_DIR));
	}
}
